'use strict';

var _deps = require('./deps');

// is
var isValid = function isValid(dir) {
  return _deps.path.parse(dir).root.length > 0;
};
var isDir = function isDir(dir) {
  return isValid(dir) && _deps.path.parse(dir).ext.length === 0;
};
var isFile = function isFile(dir) {
  return isValid(dir) && _deps.path.parse(dir).ext.length > 0;
};
var isExistent = function isExistent(dir, cb) {
  return _deps.fs.stat(dir, function (err) {
    return cb(!(err && err.code === 'ENOENT'));
  });
};
var isZeroSize = function isZeroSize(dir, cb) {
  return _deps.fs.stat(dir, function (err, stat) {
    return cb(!err && stat.size === 0);
  });
};

module.exports = {
  isValid: isValid,
  isDir: isDir,
  isFile: isFile,
  isExistent: isExistent,
  isZeroSize: isZeroSize
};